mapper.select:
SELECT award_id, award_name, award_desc, award_img, point, priority, create_time, last_edit_time, enable_status, shop_id, FROM tb_award
and award_name like '%${awardCondition.awardName}%'
and shop_id = #{awardCondition.shopId}
and enable_status = #{awardCondition.enableStatus}
ORDER BY priority DESC LIMIT #{rowIndex},#{pageSize};

SELECT count(1) FROM tb_award


        and award_name LIKE '%${awardCondition.awardName}%'



        and shop_id = #{awardCondition.shopId}



        and enable_status = #{awardCondition.enableStatus}

mapper.update:
UPDATE tb_award
award_name=#{awardName}, award_desc=#{awardDesc}, award_img#{award_img}, point=#{point}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, shop_id=#{shopId}
where award_id=#{awardId} and shop_id=#{shopId}

mapper.delete:
DELETE FROM tb_award
where
award_id = #{awardId}
and
shop_id = #{shopId}
